import React, { useState, useEffect } from "react";
import './Homepage.css'

function Homepage() {
  const [showForm, setShowForm] = useState(false);
  const [books, setBooks] = useState([]);
  const [name, setName] = useState("");
  const [author, setAuthor] = useState("");



  useEffect(() => {
    fetch("http://localhost:8000/books") // sending request to backend. 
    // The backend processes the requests and interacts with the MongoDB database.
      .then(res => res.json() // Data is retrieved and sent back to the frontend...
      .then(books => setBooks(books))); //react 
  }, []);

  function addBook(){
    setShowForm(true);
  }

  function deleteBook(id){
    fetch(`http://localhost:8000/:${id}`,{
      method: "DELETE"
    })
    .then( ()=> {
    //   setBooks((prevBooks) => prevBooks.filter((book) => book.id !== id));
    setBooks((prevBooks) => {
        const updatedBooks = prevBooks
          .filter((book) => book.id !== id)
          .map((book, index) => ({ ...book, id: index + 1 }));

        return updatedBooks;
      });
      console.log(`Deleted a book ${id}`);
    })
  }

  const handleSubmit = (event) => {
    event.preventDefault();
    // alert(`Added book ${name}, written by ${author}`)
    const id_to_add = books.length + 1
    fetch("http://localhost:8000/addbook",{
      method: "POST",
      headers: { "Content-Type": "application/json" },
      mode: "cors",
      body: JSON.stringify({"id": id_to_add,
              "name": name, 
              "author": author}),
    })

    .then(() => {
      console.log(`Added a new book ${name} by ${author}`);
      setBooks((prevBooks) => [
        ...prevBooks,
        {
          id: id_to_add,
          name: name,
          author: author,
        },
      ]);
    });
    setShowForm(false);
    setName('');
    setAuthor('');
    console.log(books)

  }

  return (
    <div className="section">
      <h3 className="title">Book Directory</h3>
      {books.map((book) => (
                    <div>
                        <b>#{book.id}: {book.name}</b>
                        <p>Written by {book.author}</p>
                        <button onClick={()=> deleteBook(book.id)}>Remove from list</button>
                        <hr></hr>
                    </div>
                ))}
      <br></br>
      <a href="#" class="block max-w-sm p-6 bg-white border border-gray-200 rounded-lg shadow hover:bg-gray-100 dark:bg-gray-800 dark:border-gray-700 dark:hover:bg-gray-700">

<h5 class="mb-2 text-2xl font-bold tracking-tight text-gray-900 dark:text-white">Noteworthy technology acquisitions 2021</h5>
<p class="font-normal text-gray-700 dark:text-gray-400">Here are the biggest enterprise technology acquisitions of 2021 so far, in reverse chronological order.</p>
</a>
      <button onClick={addBook}>Add book</button>
      {showForm && (
        <form>
          <label>Name of book:
            <input 
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)} />
          </label>
          <label>Author:
            <input type="text"
            value={author}
            onChange={(e) => setAuthor(e.target.value)}
             />
          </label>
          <button type="submit" onClick={handleSubmit}>Submit</button>
        </form>
      )}

    </div>
  );
}

export default Homepage
