import React, { useState, useEffect } from "react";
import './Homepage.css'

function Homepage() {
  const [showForm, setShowForm] = useState(false);
  const [books, setBooks] = useState([]);
  const [name, setName] = useState("");
  const [author, setAuthor] = useState("");



  useEffect(() => {
    fetch("http://localhost:8000/books") // sending request to backend. 
    // The backend processes the requests and interacts with the MongoDB database.
      .then(res => res.json() // Data is retrieved and sent back to the frontend...
      .then(books => setBooks(books))); //react 
  }, []);

  function addBook(){
    setShowForm(true);
  }

  function deleteBook(id){
    fetch(`http://localhost:8000/:${id}`,{
      method: "DELETE"
    })
    .then( ()=> {
    //   setBooks((prevBooks) => prevBooks.filter((book) => book.id !== id));
    setBooks((prevBooks) => {
        const updatedBooks = prevBooks
          .filter((book) => book.id !== id)
          .map((book, index) => ({ ...book, id: index + 1 }));

        return updatedBooks;
      });
      console.log(`Deleted a book ${id}`);
    })
  }

  const handleSubmit = (event) => {
    event.preventDefault();
    // alert(`Added book ${name}, written by ${author}`)
    const id_to_add = books.length + 1
    fetch("http://localhost:8000/addbook",{
      method: "POST",
      headers: { "Content-Type": "application/json" },
      mode: "cors",
      body: JSON.stringify({"id": id_to_add,
              "name": name, 
              "author": author}),
    })

    .then(() => {
      console.log(`Added a new book ${name} by ${author}`);
      setBooks((prevBooks) => [
        ...prevBooks,
        {
          id: id_to_add,
          name: name,
          author: author,
        },
      ]);
    });
    setShowForm(false);
    setName('');
    setAuthor('');
    console.log(books)

  }

  return (
    <div className="section">
      <h2 class="text-4xl font-extrabold dark:text-white">Books Directory</h2>
      <p class="my-4 text-lg text-gray-500">Track your favourite books here.</p>
      {books.map((book) => (
        <div>
        <div class="max-w-sm p-6 bg-white border border-gray-200 rounded-lg shadow dark:bg-gray-800 dark:border-gray-700">
            <a href="#">
                <h5 class="mb-2 text-2xl font-bold tracking-tight text-gray-900 dark:text-white">#{book.id}: {book.name}</h5>
            </a>
            <p class="mb-3 font-normal text-gray-700 dark:text-gray-400">Written by {book.author}</p>
            <a onClick={()=> deleteBook(book.id)} class="inline-flex items-center px-3 py-2 text-sm font-medium text-center text-white bg-blue-700 rounded-lg hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800">
                Remove from List
            </a>
        </div>
        <br></br>
        </div>

                    // <div>
                    //     <b>#{book.id}: {book.name}</b>
                    //     <p>Written by {book.author}</p>
                    //     <button onClick={()=> deleteBook(book.id)}>Remove from list</button>
                    //     <hr></hr>
                    // </div>
                ))}
      <br></br>
      <button onClick={addBook}>Add book</button>
      {showForm && (
        <form>
          <label>Name of book:
            <input 
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)} />
          </label>
          <label>Author:
            <input type="text"
            value={author}
            onChange={(e) => setAuthor(e.target.value)}
             />
          </label>
          <button type="submit" onClick={handleSubmit}>Submit</button>
        </form>
      )}

    </div>
  );
}

export default Homepage
