import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [message, setMessage] = useState("");
  const [books, setBooks] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8000/message") // this is the backend API
      .then((res) => res.json())
      .then((data) => setMessage(data.message));
  }, []);

  useEffect(() => {
    fetch("http://localhost:8000/books") // this is the backend API
      .then(res => res.json()
      .then(books => setBooks(books)));
  }, []);


  return (
    <div className="App">
      <h1>{message}</h1>
      <h1>Books Directory:</h1>
      {books.map((book) => (
                    <div>
                        <h2>#{book.id}: {book.name}</h2>
                        <p>Written by {book.author}</p>
                        <button>Remove from list</button>
                    </div>
                ))}
      <button>Add book</button>

    </div>
  );
}

export default App
