import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [message, setMessage] = useState("");
  const [showForm, setShowForm] = useState(false);
  const [books, setBooks] = useState([]);
  const [name, setName] = useState("");
  const [author, setAuthor] = useState("");


  useEffect(() => {
    fetch("http://localhost:8000/message") // this is the backend API
      .then((res) => res.json())
      .then((data) => setMessage(data.message));
  }, []);

  useEffect(() => {
    fetch("http://localhost:8000/books") // this is the backend API
      .then(res => res.json()
      .then(books => setBooks(books)));
  }, []);

  useEffect(() => {
    fetch("http://localhost:8000/addbook") // this is the backend API
      .then(res => res.json()
      .then((data) => setMessage(data.message)));
  }, []);

  function addBook(){
    setShowForm(true);
    fetch("http://localhost:8000/addbook",{
      method: "POST",
      headers: { "Content-Type": "application/json" },
      mode: "cors",
      body: JSON.stringify({"id": 9,
              "name": name, 
              "author": author}),
    })

    .then(() => {
      console.log("Adding a new book");
    });
  }

  const handleSubmit = (event) => {
    event.preventDefault();
    alert(`Added book ${name}, written by ${author}`)
    console.log(books)
  }

  return (
    <div className="App">
      <h1>{message}</h1>
      <h2>Book Directory:</h2>
      {books.map((book) => (
                    <div>
                        <h2>#{book.id}: {book.name}</h2>
                        <p>Written by {book.author}</p>
                        <button>Remove from list</button>
                    </div>
                ))}
      <br></br>
      <button onClick={addBook}>Add book</button>
      {showForm && (
        <form>
          <label>Name of book:
            <input 
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)} />
          </label>
          <label>Author:
            <input type="text"
            value={author}
            onChange={(e) => setAuthor(e.target.value)}
             />
          </label>
          <button type="submit" onClick={handleSubmit}>Submit</button>
        </form>
      )}

    </div>
  );
}

export default App
