import React, { useState, useEffect } from "react";
import './Homepage.css';
import Header from "./Header";

function Inspo(){
    const [websites, setWebsites] = useState([]);
    const [showAddWebsite, setAddWebsite] = useState(true);
    const [showWebForm, setShowWebForm] = useState(false);

    useEffect(() => {
        fetch("http://localhost:8000/websites")
          .then(res => res.json() 
          .then(websites => setWebsites(websites))); 
      }, []);

    function addWebsite(){
        setShowWebForm(true);
        setAddWebsite(false);
    }

    return (
        <div>
            <Header/>
            <div className="section">
            <h2 class="text-4xl font-extrabold dark:text-white">Get Inspiration âœ¨ </h2>
                <p class="my-4 text-lg text-gray-500">Find new recipes and get inspiration for your next meal!</p>
                <br></br>
                {showAddWebsite && (
                    <button class="focus:outline-none text-white bg-green-700 hover:bg-green-800 focus:ring-4 focus:ring-green-300 font-medium rounded-full text-base px-5 py-2.5 me-2 mb-2 dark:bg-green-600 dark:hover:bg-green-700 dark:focus:ring-green-800" onClick={addWebsite}>
                        + Add Website
                    </button>
                )}
                { websites.map((website) => (
                <div>
                    <div class="max-w-4xl p-6 bg-white border border-gray-200 rounded-lg shadow dark:bg-gray-800 dark:border-gray-700">
                        <a href="#">
                            <h5 class="mb-2 text-2xl font-bold tracking-tight text-gray-900 dark:text-white">{website.name}</h5>
                        </a>
                        <p class="mb-3 font-normal text-gray-700 dark:text-gray-400">{website.description}</p>
                        <br></br>
                        <a href={website.fullurl} target="_blank" class="inline-flex items-center px-3 py-2 text-sm font-medium text-center text-white bg-blue-700 rounded-full hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800">
                            Visit {website.shorturl}
                            <svg class="rtl:rotate-180 w-3.5 h-3.5 ms-2" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 14 10">
                                <path stroke="currentColor" stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M1 5h12m0 0L9 1m4 4L9 9"/>
                            </svg>
                        </a>
                    </div>

                    <br></br>
                </div>
            ))}
    </div>
</div>
    )

}
export default Inspo;
