import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [message, setMessage] = useState("");
  const [books, setBooks] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8000/message") // this is the backend API
      .then((res) => res.json())
      .then((data) => setMessage(data.message));
  }, []);

  useEffect(() => {
    fetch("http://localhost:8000/books") // this is the backend API
      .then(res => res.json()
      .then(books => setBooks(books)));
  }, []);


  return (
    <div className="App">
      <h1>{message}</h1>
      <h2>Books List:</h2>
      <p>This is where the list of books appear</p>
      <p>I do not know how to render the list of books.</p>
      <ul>
        <li>Book 1</li>
        <li>Book 2</li>
      </ul>
      <h1>Books:</h1>
      {books.map((book) => (
                    <div>
                        <h1>{book.id}</h1>
                    </div>
                ))}

    </div>
  );
}

export default App
