import React, { useState, useEffect } from "react";
// import { v4 as uuid } from "uuid";
import './Homepage.css'

function Homepage() {
//   const [message, setMessage] = useState("");
  const [showForm, setShowForm] = useState(false);
  const [books, setBooks] = useState([]);
  const [name, setName] = useState("");
  const [author, setAuthor] = useState("");


//   useEffect(() => {
//     fetch("http://localhost:8000/message") // this is the backend API
//       .then((res) => res.json())
//       .then((data) => setMessage(data.message));
//   }, []);

  useEffect(() => {
    fetch("http://localhost:8000/books") // this is the backend API
      .then(res => res.json()
      .then(books => setBooks(books)));
  }, []);

//   useEffect(() => {
//     fetch("http://localhost:8000/addbook") // this is the backend API
//       .then(res => res.json());
//   }, []);

  function addBook(){
    setShowForm(true);
    // const id_to_add = books.length + 1
    // fetch("http://localhost:8000/addbook",{
    //   method: "POST",
    //   headers: { "Content-Type": "application/json" },
    //   mode: "cors",
    //   body: JSON.stringify({"id": id_to_add,
    //           "name": name, 
    //           "author": author}),
    // })

    // .then(() => {
    //   console.log("Adding a new book");
    //   setBooks((prevBooks) => [
    //     ...prevBooks,
    //     {
    //       id: id_to_add,
    //       name: name,
    //       author: author,
    //     },
    //   ]);
    // });
  }

  function deleteBook(id){
    fetch(`http://localhost:8000/:${id}`,{
      method: "DELETE"
    })
    .then( ()=> {
      console.log(`Deleted a book ${id}`)
      setBooks((prevBooks) => prevBooks.filter((book) => book.id !== id));
      console.log(`Deleted a book ${id}`);
    })
    // .then((response) => {
    //     if (response.status === 200) {
    //       // If the deletion was successful, update the state
    //       setBooks((prevBooks) => prevBooks.filter((book) => book.id !== id));
    //       console.log(`Deleted a book ${id}`);
    //     } else {
    //       console.error(`Failed to delete book ${id}`);
    //     }
    //   })
    //   .catch((error) => {
    //     console.error('Error deleting book:', error);
    //   });
  }

  const handleSubmit = (event) => {
    event.preventDefault();
    alert(`Added book ${name}, written by ${author}`)
    const id_to_add = books.length + 1
    fetch("http://localhost:8000/addbook",{
      method: "POST",
      headers: { "Content-Type": "application/json" },
      mode: "cors",
      body: JSON.stringify({"id": id_to_add,
              "name": name, 
              "author": author}),
    })

    .then(() => {
      console.log("Adding a new book");
      setBooks((prevBooks) => [
        ...prevBooks,
        {
          id: id_to_add,
          name: name,
          author: author,
        },
      ]);
    });
    console.log(books)

  }

  return (
    <div className="section">
      {/* <h1>{message}</h1> */}
      <h3>Book Directory:</h3>
      {books.map((book) => (
                    <div>
                        <b>#{book.id}: {book.name}</b>
                        <p>Written by {book.author}</p>
                        <button onClick={()=> deleteBook(book.id)}>Remove from list</button>
                        <hr></hr>
                    </div>
                ))}
      <br></br>
      <button onClick={addBook}>Add book</button>
      {showForm && (
        <form>
          <label>Name of book:
            <input 
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)} />
          </label>
          <label>Author:
            <input type="text"
            value={author}
            onChange={(e) => setAuthor(e.target.value)}
             />
          </label>
          <button type="submit" onClick={handleSubmit}>Submit</button>
        </form>
      )}

    </div>
  );
}

export default Homepage
