import React, { useState, useEffect } from "react";
import './Homepage.css'

function Homepage() {
  const [showForm, setShowForm] = useState(false);
  const [showAddBook, setAddBook] = useState(true);
  const [books, setBooks] = useState([]);
  const [name, setName] = useState("");
  const [author, setAuthor] = useState("");
  const [showError, setError] = useState(true);



  useEffect(() => {
    fetch("http://localhost:8000/books") // sending request to backend. 
    // The backend processes the requests and interacts with the MongoDB database.
      .then(res => res.json() // Data is retrieved and sent back to the frontend...
      .then(books => setBooks(books))); //react 
  }, []);

  function addBook(){
    setShowForm(true);
    setAddBook(false);
  }

  function cancelSubmit(){
    setShowForm(false);
    setAddBook(true);
    setName('');
    setAuthor('');
  }

  function deleteBook(id){
    fetch(`http://localhost:8000/:${id}`,{
      method: "DELETE"
    })
    .then( ()=> {
    //   setBooks((prevBooks) => prevBooks.filter((book) => book.id !== id));
    setBooks((prevBooks) => {
        const updatedBooks = prevBooks
          .filter((book) => book.id !== id)
          .map((book, index) => ({ ...book, id: index + 1 }));

        return updatedBooks;
      });
      console.log(`Deleted a book ${id}`);
    })
  }

  const handleSubmit = (event) => {
    event.preventDefault();
    // alert(`Added book ${name}, written by ${author}`)
    const id_to_add = books.length + 1
    fetch("http://localhost:8000/addbook",{
      method: "POST",
      headers: { "Content-Type": "application/json" },
      mode: "cors",
      body: JSON.stringify({"id": id_to_add,
              "name": name, 
              "author": author}),
    })

    .then(() => {
      console.log(`Added a new book ${name} by ${author}`);
      setBooks((prevBooks) => [
        ...prevBooks,
        {
          id: id_to_add,
          name: name,
          author: author,
        },
      ]);
    });
    setShowForm(false);
    setAddBook(true);
    setName('');
    setAuthor('');
    console.log(books)

  }

  return (
    <div className="section">
      <h2 class="text-4xl font-extrabold dark:text-white">Bookshelf Buddy</h2>
      <p class="my-4 text-lg text-gray-500">Dive into a world where every book holds a special place in your heart.</p>
      {showAddBook && (
        <button class="focus:outline-none text-white bg-green-700 hover:bg-green-800 focus:ring-4 focus:ring-green-300 font-medium rounded-lg text-sm px-5 py-2.5 me-2 mb-2 dark:bg-green-600 dark:hover:bg-green-700 dark:focus:ring-green-800" onClick={addBook}>+ Add book</button>
      )}
      {showForm && (
        <form>
            <div class="grid gap-6 mb-6 md:grid-cols-2">
                <label class="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Name of book:
                    <input 
                        class="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500"
                        type="text"
                        value={name}
                        onChange={(e) => setName(e.target.value)} />
                 </label>

                <label class="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Author:
                <input type="text"
                    class="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500"
                    value={author}
                    onChange={(e) => setAuthor(e.target.value)}/>
                </label>

                <button type="submit" class="focus:outline-none text-white bg-green-700 hover:bg-green-800 focus:ring-4 focus:ring-green-300 font-medium rounded-lg text-sm px-5 py-2.5 me-2 mb-2 dark:bg-green-600 dark:hover:bg-green-700 dark:focus:ring-green-800" onClick={handleSubmit}>Submit</button>
                <button class="text-gray-900 bg-white border border-gray-300 focus:outline-none hover:bg-gray-100 focus:ring-4 focus:ring-gray-200 font-medium rounded-lg text-sm px-5 py-2.5 me-2 mb-2 dark:bg-gray-800 dark:text-white dark:border-gray-600 dark:hover:bg-gray-700 dark:hover:border-gray-600 dark:focus:ring-gray-700" onClick={cancelSubmit}>Cancel</button>
                {showError && (
                <div class="p-4 mb-4 text-sm text-blue-800 rounded-lg bg-blue-50 dark:bg-gray-800 dark:text-blue-400" role="alert">
                    <span class="font-medium">Remember to fill in all fields!</span> Ensure name and author fields are completed before submitting.
                </div>
                )}
            </div>
        </form>
      )}
      <br></br>
      <br></br>

      {books.map((book) => (
        <div>
            <div class="max-w-bg p-6 bg-white border border-gray-200 rounded-lg shadow dark:bg-gray-800 dark:border-gray-700">
                <a href="#">
                    <h5 class="mb-2 text-2xl font-bold tracking-tight text-gray-900 dark:text-white">{book.name}</h5>
                </a>
                <p class="mb-3 font-normal text-gray-700 dark:text-gray-400">Written by {book.author}</p>
                <button onClick={()=> deleteBook(book.id)} class="py-2.5 px-5 me-2 mb-2 text-sm font-medium text-gray-900 focus:outline-none bg-white rounded-lg border border-gray-200 hover:bg-gray-100 hover:text-blue-700 focus:z-10 focus:ring-4 focus:ring-gray-200 dark:focus:ring-gray-700 dark:bg-gray-800 dark:text-gray-400 dark:border-gray-600 dark:hover:text-white dark:hover:bg-gray-700">
                    Remove from List
                </button>
            </div>
            <br></br>
        </div>
        ))}

      <br></br>

    </div>
  );
}

export default Homepage
